package Operators;
import java.util.*;
public class Program24 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner s=new Scanner(System.in);
		    System.out.println("Enter a number");
		    int n=s.nextInt();
		    for(int i=1;i<=10;i++){
		        System.out.println(n+" * "+i+" = "+(n*i));
		    }
	}

}
